  mat.rho.p=seq(0,0.3,0.05) # ICC
  mat.icc=matrix(num.icc,length(mat.rho.p)*length(mat.ab)*length(mat.pen)*length(mat.ss)); dim(mat.icc)=c(length(mat.rho.p),length(mat.ab),length(mat.pen),length(mat.ss))
  
