  )
  
  #######################  SCENARIO 1A: mydata1A
  kappa=2.19 # dispersion parameter of negative binomial distribution
  fish=1000 # number of fish per pen
  cl.1A=c(0.8,0.9,0.95,0.99) # confidence level
  
  mydata.1A<-reactive({
    choice.1A=as.numeric(input$choice.1A)
    pen.sample.size=round(input$total.sample.size.1A/choice.1A) # sample size per pen
    loc.ab=which(min(abs(mat.ab-input$ab.1A))==abs(mat.ab-input$ab.1A))[1]
    loc.pen=which(min(abs(mat.pen-input$pen.1A))==abs(mat.pen-input$pen.1A))[1]
    ss=round(input$total.sample.size.1A/(input$pen.1A))
    loc.ss=which(min(abs(mat.ss-ss))==abs(mat.ss-ss))[1]
    no.rho.p=mat.icc[,loc.ab,loc.pen,loc.ss]
    df=data.frame(mat.rho.p,no.rho.p);
    disel=nls(no.rho.p~exp(a*mat.rho.p)+b, data=df, start = list(a = 1, b = 0))
    mat.rho.a=exp(disel$m$getPars()[1]*seq(0,0.5,0.01))+disel$m$getPars()[2]
    sol=mat.rho.a[which(min(abs(mat.rho.a-input$icc.1A))==abs(mat.rho.a-input$icc.1A))]
    clustering=log(sol-disel$m$getPars()[2])/disel$m$getPars()[1]
    icc=as.numeric(clustering) # icc of prevalence
    pr=1-(1+input$ab.1A/kappa)^(-kappa) # to match prevalence from assumed true ab
    alpha=pr*(1/icc-1)
    beta=alpha*(1/pr-1)
    pen.AB=matrix(NA,input$iteration.1A,input$pen.1A)
    for(i in 1:input$iteration.1A){pen.PR=rbeta(input$pen.1A,alpha,beta)
    pen.AB[i,]=kappa*((1/(1-pen.PR))^(1/kappa)-1)}
    RowVar <- function(x, ...) {rowSums((x - rowMeans(x, ...))^2, ...)/(dim(x)[2] - 1)}
    pen.AB=pen.AB[order(RowVar(pen.AB))[round(input$iteration.1A/2)],]
    pen.AB=pen.AB*(input$ab.1A/mean(pen.AB))
    
    withProgress(message = 'Progress time',value = 0,{
      N<-10
      for(n in 1:N){
        incProgress(1/N,detail=paste("Doing simulation"))
        
        sam.mean=matrix(NA,length(choice.1A),input$iteration.1A)
        for(i in 1:input$iteration.1A){sam.pen=matrix(NA,input$pen.1A,length(choice.1A))
        for(c in 1:length(choice.1A)){pp=sort(sample(1:input$pen.1A,choice.1A[c]))
        for(p in pp){sam.pen[p,c]=mean(sample(rnbinom(fish,size=kappa,mu=pen.AB[p]),pen.sample.size[c]))}}
        sam.mean[,i]=colMeans(sam.pen,na.rm=T)}
        
        ssm=matrix(NA,length(choice.1A),input$iteration.1A)
        for(c in 1:length(choice.1A)){ssm[c,]=sort(sam.mean[c,],na.last = T)}
        range.ab=matrix(NA,length(cl.1A)*length(choice.1A)*2); dim(range.ab)=c(2,length(choice.1A),length(cl.1A))
        for(c in 1:length(choice.1A)){for(r in 1:length(cl.1A)){range.ab[,c,r]=ssm[c,][round(input$iteration.1A*c((1-cl.1A[r])/2,1-(1-cl.1A[r])/2))]}}
      }})
    results.1A<-list(a=round(range.ab,2), b=pen.sample.size, c=pen.AB, d=choice.1A)
  })
  
  #######################  OUTPUT 1A: 
  
  plo.1A.1<-eventReactive(input$goButton.1A,{
    results.1A<-mydata.1A()
    par(oma=c(3,2,2,2),mar=c(7.5,3,3,3),cex=1.2)
    for(r in 1:length(cl.1A)){
      matplot(lwd=1.5,t(results.1A$a[,,r]),type="b",xlab="",ylab="",lty=r,col=r,pch=r,xlim=c(1,length(results.1A$d)),ylim=range(results.1A$a,na.rm=T),axes=F)
      ifelse(r==length(cl.1A),NA,par(new=T))}
    lines(x=1:length(input$choice.1A),y=rep(mean(results.1A$c),length(input$choice.1A)),lty=2)
    axis(1,1:length(results.1A$d),results.1A$d); axis(2,las=1); box()
