    loc.ab=which(min(abs(mat.ab-input$ab.3A))==abs(mat.ab-input$ab.3A))[1]
    loc.pen=which(min(abs(mat.pen-input$pen.3A))==abs(mat.pen-input$pen.3A))[1]
    ss=round(input$total.sample.size.3A/(input$pen.3A))
    loc.ss=which(min(abs(mat.ss-ss))==abs(mat.ss-ss))[1]
    no.rho.p=mat.icc[,loc.ab,loc.pen,loc.ss]
    df=data.frame(mat.rho.p,no.rho.p);
    disel=nls(no.rho.p~exp(a*mat.rho.p)+b, data=df, start = list(a = 1, b = 0))
    mat.rho.a=exp(disel$m$getPars()[1]*seq(0,0.5,0.01))+disel$m$getPars()[2]
    sol=mat.rho.a[which(min(abs(mat.rho.a-input$icc.3A))==abs(mat.rho.a-input$icc.3A))]
    clustering=log(sol-disel$m$getPars()[2])/disel$m$getPars()[1]
    icc=as.numeric(clustering) # icc of prevalence
    pr=1-(1+input$ab.3A/kappa)^(-kappa) # to match prevalence from assumed true ab
    alpha=pr*(1/icc-1)
    beta=alpha*(1/pr-1)
    pen.AB=matrix(NA,input$iteration.3A,input$pen.3A)
    for(i in 1:input$iteration.3A){pen.PR=rbeta(input$pen.3A,alpha,beta)
    pen.AB[i,]=kappa*((1/(1-pen.PR))^(1/kappa)-1)}
    RowVar <- function(x, ...) {rowSums((x - rowMeans(x, ...))^2, ...)/(dim(x)[2] - 1)}
    pen.AB=pen.AB[order(RowVar(pen.AB))[round(input$iteration.3A/2)],]
    pen.AB=pen.AB*(input$ab.3A/mean(pen.AB))
    
    withProgress(message = 'Progress time',value = 0,{
      N<-10
      for(n in 1:N){
        incProgress(1/N,detail=paste("Doing simulation"))
        
        sam.mean=matrix(NA,length(choice.3A),input$iteration.3A)
        for(i in 1:input$iteration.3A){sam.pen=matrix(NA,input$pen.3A,length(choice.3A))
        for(c in 1:length(choice.3A)){pp=sort(sample(1:input$pen.3A,choice.3A[c]))
        for(p in pp){sam.pen[p,c]=mean(sample(rnbinom(fish,size=kappa,mu=pen.AB[p]),pen.sample.size[c]))}}
        sam.mean[,i]=colMeans(sam.pen,na.rm=T)}
        ssm=matrix(NA,length(choice.3A),input$iteration.3A); for(c in 1:length(choice.3A)){ssm[c,]=sort(sam.mean[c,],na.last = T)}
        cutline=numeric(); 
        for(p in 1:length(choice.3A)){cutline[p]=ifelse(input$ab.3A>=input$th.3A,mean(ssm[p,]>=input$th.3A),mean(ssm[p,]<input$th.3A))}
        
      }})
    results.3A<-list(a=cutline, b=ssm, c=pen.sample.size, d=choice.3A)
  })
  
